<?php get_header(); ?><section id="primary">	<div id="content" role="main">		<header class="page-header">			<h1 class="page-title">Archives <span>				<?php if ( is_day() ) : ?>					<?php echo get_the_date(); ?>				<?php elseif ( is_month() ) : ?>					<?php echo get_the_date('F Y'); ?>				<?php elseif ( is_year() ) : ?>					<?php echo get_the_date('Y'); ?>				<?php endif; ?>				</span>			</h1>		</header>		<?php while ( have_posts() ) : the_post(); ?>			<?php				get_template_part( 'content');			?>		<?php endwhile; ?>				<?php wp_paginate();?>	</div></section><?php get_sidebar(); ?><?php get_footer(); ?>